<?php/** * Description of ZemePresenter * * @author Martin Patyk */namespace App\AdminModule\Presenters;use App\Grids\FkGrid;use App\Model\ZemeModel;use DibiException;use App\Form\Admin\Edit\FkBaseForm as EditFkBaseForm;use App\Form\Admin\Add\FkBaseForm as AddFkBaseForm;use Nette\Application\AbortException;use Nette\Database\Context;use Nette\Diagnostics\Debugger;use Nette\InvalidArgumentException;class ZemePresenter extends AdminbasePresenter{    /** @var ZemeModel */    private $zemeModel;    /** @var Context */    private $zemeContext;    public function __construct(ZemeModel $zemeModel, Context $zemeContext)    {        parent::__construct();        $this->zemeModel = $zemeModel;        $this->zemeContext = $zemeContext;    }    /**     * Cast DEFAULT, definice Gridu     */    protected function createComponentGrid()    {        return new FkGrid($this->zemeContext->table('zeme'));    }    public function renderDefault()    {        $this->setView('../_default');    }    /*************************************** PART ADD **************************************/    public function renderAdd()    {        $this->setView('../_add');    }    public function createComponentAdd()    {        $form = new AddFkBaseForm;        $form->onSuccess[] = callback($this, 'add');        return $form;    }    /**     * @throws AbortException     */    public function add(AddFkBaseForm $form)    {        try {            $v = $form->getValues();            $this->zemeModel->insert($v);        } catch (DibiException $exc) {            Debugger::log($exc->getMessage());            $form->addError('Nový záznam nebyl přidán');        }        $this->flashMessage('Nový záznam byl přidán');        $this->redirect('default');    }    /**     * Cast EDIT     * @param int $id Identifikator polozky     * @throws AbortException     */    public function renderEdit($id)    {        try {            $this->setView('../_edit');            //	nactu hodnoty pro editaci, pritom overim jestli hodnoty existuji            $v = $this->zemeModel->fetch($id);            //	odeberu idecko z pole            $v->offsetUnset('id');            //	upravene hodnoty odeslu do formulare            $this['edit']->setDefaults(array('id' => $id, 'new' => $v));        } catch (InvalidArgumentException $exc) {            $this->flashMessage($exc->getMessage());            $this->redirect('default');        }    }    public function createComponentEdit()    {        $form = new EditFkBaseForm;        $form->onSuccess[] = callback($this, 'edit');        return $form;    }    /**     * @throws AbortException     */    public function edit(EditFkBaseForm $form)    {        try {            $v = $form->getValues();            $this->zemeModel->update($v['new'], $v['id']);        } catch (DibiException $exc) {            Debugger::log($exc->getMessage());            $form->addError('Záznam nebyl změněn');        }        $this->flashMessage('Záznam byl úspěšně změněn');        $this->redirect('default');    }    /**     * Cast DROP     * @param int $id Identifikator polozky     * @throws AbortException     */    public function actionDrop($id)    {        try {            $this->zemeModel->fetch($id);            $this->zemeModel->remove($id);            $this->flashMessage('Položka byla odebrána'); // Položka byla odebrána            $this->redirect('Zeme:default');    //	change it !!!        } catch (InvalidArgumentException $exc) {            $this->flashMessage($exc->getMessage());            $this->redirect('Zeme:default');    //	change it !!!        } catch (DibiException $exc) {            $this->flashMessage('Položka nebyla odabrána, zkontrolujte závislosti na položku');            $this->redirect('Zeme:default');    //	change it !!!        }    }}