<?phpnamespace App\Components\WorkLog;use App\Model\IncidentLogModel;use Nette\Application\UI\Control;use Nette\Database\Explorer;class WorkLogControl extends Control{    /** @var IncidentLogModel $model */    private readonly IncidentLogModel $model;    public function __construct(Explorer $explorer)    {        $this->model = new IncidentLogModel($explorer);    }    public function render(int $id): void    {        $template = $this->getTemplate();        $template->setFile(__DIR__ . '/wl.latte');//        $template->getLatte()->addFilter('texyWl', [$this->texy, 'process']);        $this->template->items = $this->model->fetchAllByIncidentId($id);        $template->render();    }}