<?phpnamespace App\Grids\Admin;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of FirmaGrid * * @author Martin Patyk */class FirmaGrid extends Grid{    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('*'));        $this->addColumn('nazev', 'Název');        $this->addColumn('ico', 'IČO');        $this->addColumn('dic', 'DIČ');        $this->addColumn('ulice', 'Ulice');        $this->addColumn('obec', 'Obec');        $this->addButton("fakturka", "Nova faktura")            ->setClass("fakturka")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("Faktura:add", $row['id']);            })            ->setAjax(FALSE);        $this->addButton("edit", "Upravit")            ->setClass("edit")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("edit", $row['id']);            })            ->setAjax(FALSE);        $this->addButton("delete", "Smazat")            ->setClass("delete")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("drop", $row['id']);            })            ->setConfirmationDialog(function ($row) {                return "Opravdu chcete smazat $row[nazev] ?";            })            ->setAjax(FALSE);        //Předáme zdroj        $this->setDataSource($source);    }}