<?phpnamespace App\Grids\Admin;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of FirmaGrid * * @author Martin Patyk */class FirmaGrid extends Grid{    use AdminGridButtonTrait;    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('*'));        $this->addColumn('nazev', 'Název');        $this->addColumn('ico', 'IČO');        $this->addColumn('dic', 'DIČ');        $this->addColumn('ulice', 'Ulice');        $this->addColumn('obec', 'Obec');        $this->newInvoiceButton($presenter);        $this->editButton($presenter);        $this->deleteButton($presenter);        //Předáme zdroj        $this->setDataSource($source);    }}