<?phpnamespace Gridy;use App\Grids\Admin\AdminGridButtonTrait;use Nette\Database\Table\Selection;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of FrontaOsobaGrid * * @author Martin Patyk */class FrontaOsobaGrid extends Grid{    use AdminGridButtonTrait;    private $model;    public function __construct(Selection $db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource(            $this->model                ->select('fronta_osoba.id, fronta.nazev AS fronta, CONCAT(osoba.jmeno, " ",osoba.prijmeni) AS osoba'));        $this->addColumn('id', 'ID', '60px');        $this->addColumn('fronta', 'Fronta');        $this->addColumn('osoba', 'Osoba');        $this->editButton($presenter);        $this->deleteButton($presenter);        //Předáme zdroj        $this->setDataSource($source);    }}