<?phpnamespace Gridy;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of FrontaOsobaGrid * * @author Martin Patyk */class FrontaOsobaGrid extends Grid{    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource(            $this->model                ->select('fronta_osoba.id, fronta.nazev AS fronta, osoba.jmeno|| " " ||osoba.prijmeni AS osoba'));        $this->addColumn('id', 'ID', '60px');        $this->addColumn('fronta', 'Fronta');        $this->addColumn('osoba', 'Osoba');        $this->addButton("edit", "Upravit")            ->setClass("edit")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("edit", $row['id']);            })            ->setAjax(FALSE);        $this->addButton("delete", "Smazat")            ->setClass("delete")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("drop", $row['id']);            })            ->setConfirmationDialog(function ($row) {                return "Opravdu chcete smazat $row[id] ?";            })            ->setAjax(FALSE);        //Předáme zdroj        $this->setDataSource($source);    }}