<?phpnamespace App\Grids\Admin;use Nette\Database\Table\Selection;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of OdCiGrid * @deprecated * @author Martin Patyk */class OdCiGrid extends Grid{    use AdminGridButtonTrait;    private $model;    public function __construct(Selection $db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('od_ci.id,od,ci.nazev AS ci'));        $this->addColumn('od', 'Odesilatel');        $this->addColumn('ci', 'Nazev CI');        $this->editButton($presenter);        $this->deleteButton($presenter);        $this->setDataSource($source);    }}