<?phpnamespace App\Grids\Admin;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of OdCiGrid * * @author Martin Patyk */class OdCiGrid extends Grid{    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('od_ci.id,od,ci.nazev AS ci'));        $this->addColumn('od', 'Odesilatel');        $this->addColumn('ci', 'Nazev CI');        $this->addButton("edit", "Upravit")            ->setClass("edit")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("edit", $row['id']);            })            ->setAjax(FALSE);        $this->addButton("delete", "Smazat")            ->setClass("delete")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("drop", $row['id']);            })            ->setConfirmationDialog(function ($row) {                return "Opravdu chcete smazat $row[od] ?";            })            ->setAjax(FALSE);        $this->setDataSource($source);    }}