<?phpnamespace App\Grids\Admin;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of OsobaGrid * * @author Martin Patyk */class OsobaGrid extends Grid{    use AdminGridButtonTrait;    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('osoba.id, jmeno, prijmeni, typ_osoby.nazev AS typ_osoby, firma.nazev AS firma'));        $this->addColumn('jmeno', 'Jméno');        $this->addColumn('prijmeni', 'Příjmení');        $this->addColumn('typ_osoby', 'Typ osoby');        $this->addColumn('firma', 'Firma');        $this->newPasswordButton($presenter);        $this->editButton($presenter);        $this->deleteButton($presenter);        //Předáme zdroj        $this->setDataSource($source);    }}