<?phpnamespace App\Grids\Admin;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of OsobaGrid * * @author Martin Patyk */class OsobaGrid extends Grid{    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('osoba.id, jmeno, prijmeni, typ_osoby.nazev AS typ_osoby, firma.nazev AS firma'));        $this->addColumn('jmeno', 'Jméno', '100px');        $this->addColumn('prijmeni', 'Příjmení', '100px');        $this->addColumn('typ_osoby', 'Typ osoby', '120px');        $this->addColumn('firma', 'Firma', '120px');        $this->addButton('nove_heslo', 'Vygeneruj nové heslo')            ->setClass('pass')            ->setLink(function ($row) use ($presenter) {                return $presenter->link("generujNoveHeslo", $row['id']);            });        $this->addButton("edit", "Upravit")            ->setClass("edit")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("edit", $row['id']);            })            ->setAjax(FALSE);        $this->addButton("delete", "Smazat")            ->setClass("delete")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("drop", $row['id']);            })            ->setConfirmationDialog(function ($row) {                return "Opravdu chcete smazat $row[jmeno] ?";            })            ->setAjax(FALSE);        //Předáme zdroj        $this->setDataSource($source);    }}