<?phpnamespace App\Grids\Admin;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of PolozkyFakturyGrid * * @author Martin Patyk */class PolozkyFakturyGrid extends Grid{    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model            ->select('faktura_polozka.id AS id')            ->select('cssclass')            ->select('faktura_polozka.nazev')            ->select('CONCAT(dph.procent,"%") AS procent')            ->select('CONCAT(pocet_polozek,jednotka.zkratka) AS pocet_polozek')            ->select('cena')            ->select('koeficient_cena')            ->select('sleva')            ->select('ROUND(pocet_polozek*cena*dph.koeficient*koeficient_cena*(1-(sleva*0.01)),2) AS cena_celkem')        );        $this->addColumn('id', 'ID');        $this->addColumn('nazev', 'Název');        $this->addColumn('cssclass', 'cssclass');        $this->addColumn('procent', 'DPH');        $this->addColumn('pocet_polozek', 'Pocet');        $this->addColumn('koeficient_cena', 'Koeficient');        $this->addColumn('sleva', 'Sleva');        $this->addColumn('cena', 'Cena/jednotka');        $this->addColumn('cena_celkem', 'Cena celkem');        $this->setDefaultOrder("faktura_polozka.id ASC");        $this->addButton("edit", "Upravit")            ->setClass("edit")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("FakturaPolozka:edit", $row['id']);            })            ->setAjax(FALSE);        $this->addButton("delete", "Smazat")            ->setClass("delete")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("FakturaPolozka:drop", $row['id']);            })            ->setConfirmationDialog(function ($row) {                return "Opravdu chcete smazat $row[nazev] ?";            })            ->setAjax(FALSE);        //Předáme zdroj        $this->setDataSource($source);    }}