<?phpnamespace App\Grids\Admin;use Nette\Database\Table\Selection;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of SlaGrid * @deprecated * @author Martin Patyk */class SlaGrid extends Grid{    use AdminGridButtonTrait;    private $model;    public function __construct(Selection $db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model            ->select('sla.id AS id, ' .                'tarif.nazev AS tarif, ' .                'priorita.nazev AS priorita, ' .                'sla.cena_koeficient AS cenaKoe, ' .                'sla.cena_koeficient*tarif.cena AS cenaCelkem'));        $this->addColumn('tarif', 'Jméno tarifu');        $this->addColumn('priorita', 'Priorita');        $this->addColumn('cenaKoe', 'Cena koeficient');        $this->addColumn('cenaCelkem', 'Cena za ukon celkem');        $this->editButton($presenter);        //Předáme zdroj        $this->setDataSource($source);    }}