<?phpnamespace App\Grids\Admin;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of SlaGrid * * @author Martin Patyk */class SlaGrid extends Grid{    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('sla.id AS id, tarif.nazev AS tarif, priorita.nazev AS priorita, sla.cena_koeficient AS cenaKoe, sla.cena_koeficient*tarif.cena AS cenaCelkem'));        $this->addColumn('tarif', 'Jméno tarifu');        $this->addColumn('priorita', 'Priorita');        $this->addColumn('cenaKoe', 'Cena koeficient');        $this->addColumn('cenaCelkem', 'Cena za ukon celkem');        $this->addButton("edit", "Upravit")            ->setClass("edit")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("edit", $row['id']);            })            ->setAjax(FALSE);        //Předáme zdroj        $this->setDataSource($source);    }}