<?phpnamespace App\Grids\Admin;use Nette\Database\Table\Selection;use Nette\Utils\Html;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\DuplicateButtonException;use NiftyGrid\DuplicateColumnException;use NiftyGrid\Grid;/** * Description of TarifGrid * @deprecated * @author Martin Patyk */class TarifGrid extends Grid{    use AdminGridButtonTrait;    private $model;    public function __construct(Selection $db)    {        parent::__construct();        $this->model = $db;    }    /**     * @throws DuplicateButtonException     * @throws DuplicateColumnException     */    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('*'));        $this->addColumn('nazev', 'Název')            ->setRenderer(function ($row) use ($presenter) {                return Html::el('a')->setText($row['nazev'])->href($presenter->link("Sla:", $row['id']));            });        $this->addColumn('cena', 'Cana tarifu');        $this->editButton($presenter);        $this->deleteButton($presenter);        //Předáme zdroj        $this->setDataSource($source);    }}