<?phpnamespace App\Grids\Admin;use Nette\Database\Table\Selection;use NiftyGrid\DataSource\NDataSource;use NiftyGrid\Grid;/** * Description of TypIncidentGrid * * @author Martin Patyk */class TypIncidentGrid extends Grid{    use AdminGridButtonTrait;    private $model;    public function __construct(Selection $db)    {        parent::__construct();        $this->model = $db;    }    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('*'));        $this->addColumn('nazev', 'Název');        $this->addColumn('zkratka', 'Zkratka');        $this->addColumn('koeficient_cena', 'Koeficient cena');        $this->addColumn('koeficient_cas', 'Koeficient čas');        $this->editButton($presenter);        $this->deleteButton($presenter);        //Předáme zdroj        $this->setDataSource($source);    }}