<?phpnamespace Gridy;/** * Description of FkGrid * * @author Martin Patyk */use NiftyGrid\DataSource\NDataSource;use NiftyGrid\Grid;class FkGrid extends Grid{    private $model;    public function __construct($db)    {        parent::__construct();        $this->model = $db;    }    protected function configure($presenter)    {        //Vytvoříme si zdroj dat pro Grid        //Při výběru dat vždy vybereme id        $source = new NDataSource($this->model->select('*'));        $this->addColumn('nazev', 'Název');        $this->addButton("edit", "Upravit")            ->setClass("edit")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("edit", $row['id']);            })            ->setAjax(FALSE);        $this->addButton("delete", "smazat")            ->setClass("delete")            ->setLink(function ($row) use ($presenter) {                return $presenter->link("drop", $row['id']);            })            ->setConfirmationDialog(function ($row) {                return "Opravdu chcete smazat $row[nazev]?";            })            ->setAjax(FALSE);        //Předáme zdroj        $this->setDataSource($source);    }}