<?phpnamespace App\Model;use dibi;use DibiException;/** * Description of FormatDatumModel * * @author Martin Patyk */final class FormatDatumModel extends BaseModel{    /** @var string nazev tabulky */    protected $name = 'format_datum';    /**     * Vrati nazev a primarni klic v paru k pouziti nacteni cizich klicu ve formulari     * @return string     * @throws DibiException     */    public static function fetchPairs()    {        return dibi::fetchPairs('SELECT [id], [nazev] FROM [format_datum] ORDER BY [nazev]');    }}