<?phpnamespace App\Model;/** * Description of FormatDatumModel * * @author Martin Patyk */final class FormatDatumModel extends BaseModel{    /** @var string nazev tabulky */    protected $name = 'format_datum';    /**     * Vrati nazev a primarni klic v paru k pouziti nacteni cizich klicu ve formulari     * @return array     */    public function fetchPairs(): array    {        return $this->explorer->table($this->name)->order('nazev')->fetchPairs('id', 'nazev');    }}